$(document).ready(function () {
    $('.color_info .form-check input[type=\'radio\']').change(function () {
        var pid = this.id;
        $(this).parents('.prod_block').find('.p_image').addClass('hide');
        $(this).parents('.prod_color').find('.p_image').addClass('hide');
        $('#img-' + pid).removeClass('hide');
    });
});
